}

new Dialog({
  title: 'ğŸŒ² Incontri Selvaggi',
  content: `
    <div style="padding: 10px;">
      <h3>Genera Incontro Casuale</h3>
      <div style="margin: 15px 0;">
        <label for="terrain-type" style="font-weight: bold;">Tipo di Terreno:</label>
        <select id="terrain-type" style="width: 100%; margin-top: 5px; padding: 5px;">
          <option value="forest">ğŸŒ² Foresta</option>
