command:
// Macro per Intrattenimento in Taverna
if (!game.modules.get('brancalonia-bigat')?.active) {
  ui.notifications.error('Modulo Brancalonia non attivo!');
  return;
}

const entertainment = game.brancalonia?.TavernEntertainment;
if (!entertainment) {
  ui.notifications.error('Sistema Intrattenimento non disponibile!');
  return;
}

const tokens = canvas.tokens.controlled;
if (tokens.length === 0) {
  ui.notifications.warn('Seleziona un token!');
  return;
}

const actor = tokens[0].actor;

new Dialog({
  title: 'ğŸª Intrattenimento in Taverna',
  content: `
    <div style="padding: 10px;">
      <h3>${actor.name} si esibisce!</h3>
      <div style="margin: 15px 0;">
        <label for="performance-type" style="font-weight: bold;">Tipo di Esibizione:</label>
        <select id="performance-type" style="width: 100%; margin-top: 5px;">
          <option value="music">ğŸµ Musica</option>
          <option value="storytelling">ğŸ“– Racconti</option>
          <option value="juggling">ğŸ¤¹ Giocoleria</option>
          <option value="comedy">ğŸ˜„ Commedia</option>
          <option value="dance">ğŸ’ƒ Danza</option>
          <option value="magic-tricks">âœ¨ Trucchi di Magia</option>
        </select>
      </div>
      <div style="margin: 15px 0;">
        <label for="crowd-mood" style="font-weight: bold;">Umore del Pubblico:</label>
        <select id="crowd-mood" style="width: 100%; margin-top: 5px;">
          <option value="hostile">ğŸ˜  Ostile</option>
          <option value="bored">ğŸ˜‘ Annoiato</option>
          <option value="neutral" selected>ğŸ˜ Neutrale</option>
          <option value="interested">ğŸ™‚ Interessato</option>
          <option value="excited">ğŸ˜ƒ Eccitato</option>
        </select>
      </div>
      <div style="margin: 15px 0;">
        <label for="tavern-quality" style="font-weight: bold;">QualitÃ  della Taverna:</label>
        <select id="tavern-quality" style="width: 100%; margin-top: 5px;">
          <option value="dive">ğŸšï¸ Bettola</option>
          <option value="common" selected>ğŸ  Comune</option>
          <option value="quality">ğŸ›ï¸ Di QualitÃ </option>
          <option value="noble">ğŸ‘‘ Nobile</option>
        </select>
      </div>
      <hr>
      <p style="text-align: center; font-style: italic;">"Un buon spettacolo vale piÃ¹ di cento monete!"</p>
    </div>
  `,
  buttons: {
    perform: {
      icon: '<i class="fas fa-microphone"></i>',
      label: 'Esibisciti!',
      callback: async (html) => {
        const type = html.find('#performance-type').val();
        const mood = html.find('#crowd-mood').val();
        const quality = html.find('#tavern-quality').val();

        // Calcola modificatori
        const moodMod = {hostile: -5, bored: -2, neutral: 0, interested: 2, excited: 5}[mood];
        const qualityMod = {dive: -2, common: 0, quality: 2, noble: 4}[quality];

        // Tira Performance
        const roll = await actor.rollSkill('prf', {
          flavor: `Esibizione: ${type}`,
          situational: moodMod + qualityMod
        });

        const total = roll.total;
        let result, earnings;

        if (total >= 20) {
          result = "Spettacolare! Il pubblico Ã¨ in delirio!";
          earnings = 3 + Math.floor(Math.random() * 3) + 1;
        } else if (total >= 15) {
          result = "Ottima esibizione! Applausi scroscianti!";
          earnings = 2 + Math.floor(Math.random() * 2);
        } else if (total >= 10) {
          result = "Discreta esibizione. Qualche applauso.";
          earnings = 1 + Math.floor(Math.random() * 2);
        } else if (total >= 5) {
          result = "Esibizione mediocre. Poco interesse.";
          earnings = Math.floor(Math.random() * 2);
        } else {
          result = "Disastro! Ti fischiano e lanciano ortaggi!";
          earnings = 0;
          // PossibilitÃ  di rissa
          if (Math.random() < 0.3) {
            ChatMessage.create({
              content: `<div class="brancalonia-event">
                <h3>âš ï¸ Rissa in arrivo!</h3>
                <p>Il pubblico Ã¨ talmente infuriato che sta per scoppiare una rissa!</p>
              </div>`
            });
          }
        }

        ChatMessage.create({
          content: `<div class="brancalonia-performance">
            <h3>ğŸ­ ${actor.name} - ${type}</h3>
            <p><strong>Risultato:</strong> ${result}</p>
            ${earnings > 0 ? `<p><strong>Guadagno:</strong> ${earnings} monete d'oro</p>` : ''}
          </div>`,
          speaker: ChatMessage.getSpeaker({actor: actor})
        });

        if (earnings > 0) {
          actor.update({'system.currency.gp': actor.system.currency.gp + earnings});
        }
      }
    },
    gamble: {
      icon: '<i class="fas fa-dice"></i>',
      label: 'Gioca d\'Azzardo',
      callback: () => {
        entertainment.startGambling(actor);
      }
    },
    drink: {
      icon: '<i class="fas fa-beer"></i>',
      label: 'Gara di Bevute',
      callback: () => {
        entertainment.drinkingContest(actor);
      }
    },
    cancel: {
      icon: '<i class="fas fa-times"></i>',
      label: 'Chiudi'
    }
  },
  default: 'perform'
}).render(true);
